SELECT C.CARTNO,C.MEMBERNO,C.BOOKNO,C.CCOUNT, B.BTITLE,B.BPRICESELL,B.BDISCOUNT,B.BPRICE, D.BIMG,CT.BCATEGORY1, M.MEMBERNO FROM M_CART C, M_BOOK B, M_BOOK_DETAIL D, M_BOOK_BCATEGORY CT, M_MEMBER M WHERE C.BOOKNO=B.BOOKNO AND C.BOOKNO=D.BOOKNO AND D.BCATEGORYNO=CT.BCATEGORYNO AND C.MEMBERNO=M.MEMBERNO AND C.CARTNO IN
#{item}
AND C.MEMBERNO = 1

SELECT SUM(B.BPRICESELL*C.CCOUNT) FROM M_BOOK B,M_CART C,M_MEMBER M WHERE B.BOOKNO=C.BOOKNO AND C.MEMBERNO=M.MEMBERNO AND C.CARTNO IN
#{item}
AND C.MEMBERNO = 1

SELECT SUM(CCOUNT) FROM M_CART 
WHERE MEMBERNO=1 AND CARTNO IN

#{item}
